function [backwindow_fix,backwindow] = PORCfix4a(backwindow_fix,backwindow,fixrect,occlines,penW,grayblack)%% PORCfix4a.m%% function for Perceptual Organization Reverse Correlation experiments% generates fixation screen (Exp. 1a)% Called by: PORCpractice4a.m PORCquest4a.m%% Variables:%%% Written by Adam Greenberg, JHU/PBS% Sept, 2010%% draw fixation box on offscreen window% Screen('DrawLines', backwindow_fix, fixlines, penW, chargray);Screen('FillRect', backwindow_fix, grayblack, fixrect);% draw fixation box and other elementsScreen('FillRect', backwindow, grayblack, fixrect);Screen('DrawLines', backwindow, occlines, penW, grayblack);