function [backwindow_fix,backwindow] = PLOTfix(backwindow_fix,backwindow,fixrect,grayblack, im_in,window)%% PORCfix4a.m%% function for Perceptual Organization Reverse Correlation experiments% generates fixation screen (Exp. 1a)% Called by: PORCpractice4a.m PORCquest4a.m%% Variables:%%% Written by Adam Greenberg, JHU/PBS% Sept, 2010%% draw fixation box on offscreen window%Screen('DrawLines', backwindow_fix, fixlines, penW, chargray);%Screen('FillRect', backwindow_fix, grayblack, fixrect);% Get the screen numbersscreens = Screen('Screens');% Draw to the external screen if avaliablescreenNumber = max(screens);% Define black and whitewhite = WhiteIndex(screenNumber);black = BlackIndex(screenNumber);grey = white / 2;inc = white - grey;% Set up alpha-blending for smooth (anti-aliased) linesScreen('BlendFunction', backwindow, 'GL_SRC_ALPHA', 'GL_ONE_MINUS_SRC_ALPHA');% Set up alpha-blending for smooth (anti-aliased) linesScreen('BlendFunction', backwindow_fix, 'GL_SRC_ALPHA', 'GL_ONE_MINUS_SRC_ALPHA');% grey out the offscreen windowScreen('FillRect', backwindow, grey); % draw fixation box on offscreen window% Screen('DrawLines', backwindow_fix, fixlines, penW, chargray);%Screen('FillRect', backwindow, grayblack, fixrect); % EJD COMMENTED OUT%FIXATION PT CALL.. IMAGES ALREADY HAVE FIX PT..% draw the imageimage_tex = Screen('MakeTexture', window, im_in);Screen('DrawTexture', backwindow_fix, image_tex)%Screen('DrawLines', backwindow, occlines, penW, grayblack);