function [backwindow_fix,backwindow] = PLOTfix(backwindow_fix,backwindow,fixrect,grayblack, im_in,window)% Plots fixation screen..% Get the screen numbersscreens = Screen('Screens');% Draw to the external screen if avaliablescreenNumber = max(screens);% Define black and whitewhite = WhiteIndex(screenNumber);black = BlackIndex(screenNumber);grey = white / 2;inc = white - grey;% Set up alpha-blending for smooth (anti-aliased) linesScreen('BlendFunction', backwindow, 'GL_SRC_ALPHA', 'GL_ONE_MINUS_SRC_ALPHA');% Set up alpha-blending for smooth (anti-aliased) linesScreen('BlendFunction', backwindow_fix, 'GL_SRC_ALPHA', 'GL_ONE_MINUS_SRC_ALPHA');% grey out the offscreen windowScreen('FillRect', backwindow, grey); % draw the imageimage_tex = Screen('MakeTexture', window, im_in);Screen('DrawTexture', backwindow_fix, image_tex)