function [backwindow] = PORClinkRevCor_v1(mainsrc, window, backwindow,trialprop, trial_con, image_in,fixrect,grayblack)%% PORClinkRevCor_v1.m%% Function for reverse correlation experiments% Displays base images.%% Variables:%% Written by Ethan Duwell% July, 2022% Updated for RevCorr on 4/14/23%% This script began as Adam Greenberg's PORClinkL4a.m. Ethan Duwell% initially modified it to display shapes filled with textures % (instead of shapes of variable luminance)  %% This version is intended for the Reverse Correlation Project%% First some basic setup% Get the screen numbersscreens = Screen('Screens');% Draw to the external screen if avaliablescreenNumber = max(screens);% Define black and whitewhite = WhiteIndex(screenNumber);black = BlackIndex(screenNumber);grey = white / 2;inc = white - grey;% Set up alpha-blending for smooth (anti-aliased) linesScreen('BlendFunction', backwindow, 'GL_SRC_ALPHA', 'GL_ONE_MINUS_SRC_ALPHA');%% Draw the shapes/images to the final output offscreen window% draw the imageimage_tex = Screen('MakeTexture', window, image_in);Screen('DrawTexture', backwindow, image_tex)end